mapper.select:
		SELECT * FROM
			(
				SELECT ROWNUM as rn, notice.* FROM
					(
						SELECT * FROM NoticeBoard

						ORDER BY nbNum DESC
					) notice 
		)

  		WHERE rn > (#{pageNum}-1) * #{countPerPage}
  		AND rn <= #{pageNum} * #{countPerPage}

mapper.update:
UPDATE NoticeBoard SET
	nbLookCount=nbLookCount+1
WHERE nbNum = #{nbNum}

mapper.select:
SELECT COUNT(*) FROM NoticeLikely
WHERE nbNum = #{nbNum }
AND memberNum = #{memberNum }

SELECT COUNT(*) FROM NoticeReport
WHERE nbNum = #{nbNum }
AND memberNum = #{memberNum }
