mapper.insert:
INSERT INTO NoticeReply
(
	nrNum, nbNum, nrContent, memberNum
)
VALUES
(
	noticeReply_seq,NEXTVAL, #{nrNum}, #{nbNum}, 
	#{nrContent}, #{memberNum}
)

mapper.select:
SELECT * FROM NoticeReply
WHERE nrNum = #{nrNum}
ORDER BY nrNum DESC

mapper.update:
UPDATE NoticeReply
SET
nrContent = #{nrContent}
WHERE nrNum = #{nrNum}
