mapper.update:
 		UPDATE PlaceBoard
 		SET pbLookCount = pbLookCount + 1
		WHERE pbNum=#{pbNum}

mapper.select:
SELECT COUNT(*) FROM PlaceLikely
WHERE pbNum = #{pbNum}
AND memberNum = #{memberNum}

mapper.delete:
 	  DELETE FROM PlaceLikely
 	  WHERE pbNum = #{pbNum}
	  AND memberNum = #{memberNum}

mapper.select:
SELECT COUNT(*) FROM PlaceReport
WHERE pbNum = #{pbNum}
AND memberNum = #{memberNum}
