mapper.select:

   SELECT * FROM
   (
		SELECT ROWNUM as rn, tbl.*
		FROM
		   (
		   SELECT * FROM CourseBoard


		   ORDER BY cbNum DESC
		   ) tbl
	)

	WHERE rn > (#{pageNum} - 1) * #{countPerPage}
	AND rn <= #{pageNum} * #{countPerPage}

mapper.update:
UPDATE CourseBoard
SET cbCategory=#{cbCategory}, cbTitle=#{cbTitle}, cbContent=#{cbContent}, cbYouCode=#{cbYouCode}
WHERE cbNum=#{cbNum}

UPDATE CourseBoard
SET cbLookCount = cbLookCount + 1
WHERE cbNum=#{cbNum}

UPDATE CourseBoard
SET crCount = #{crCount}
WHERE cbNum = #{cbNum}

mapper.select:
SELECT COUNT(*) FROM CourseLikely
WHERE cbNum = #{cbNum}
AND memberNum = #{memberNum}

mapper.delete:
DELETE FROM CourseLikely
WHERE cbNum = #{cbNum}
AND memberNum = #{memberNum}

mapper.select:
SELECT COUNT(*) FROM CourseReport
WHERE cbNum = #{cbNum}
AND memberNum = #{memberNum}
