mapper.insert:
INSERT INTO DietReply
(
	drNum, dbNum, drContent, memberNum
)
VALUES
(
	dietReply_seq.NEXTVAL, #{dbNum}, 
	#{drContent}, #{memberNum}
)

mapper.select:
SELECT * FROM DietReply
WHERE dbNum = #{dbNum}
ORDER BY drNum DESC

mapper.update:
UPDATE DietReply
SET
drContent = #{drContent}
WHERE drNum = #{drNum}
