mapper.select:
SELECT * FROM
(
	SELECT ROWNUM as rn, freereply.* FROM
	(
		SELECT fr.*
		FROM FreeBoard fb, FreeReply fr
		WHERE fb.fbNum = fr.fbNum
		ORDER BY fr.frRegDate DESC
	) freereply
)

WHERE rn > (#{pageNum}-1)*#{countPerPage}
AND rn <= #{pageNum} * #{countPerPage}

mapper.update:
UPDATE FreeReply SET
	frContent = #{frContent}
WHERE frNum = #{frNum}
